import { Context } from "../context/context.js";
import ora from "ora";
import { Colors } from "./colors.js";
export const useSpinners = Context.memo(() => {
    const spinners = [];
    return spinners;
});
export function createSpinner(options) {
    const spinners = useSpinners();
    const next = ora(options);
    spinners.push(next);
    Colors.mode("line");
    return next;
}
