assetPublishing.reportCdkAssetsVersion()

export const reportCdkAssetsVersion = (program) => program.command("reportCdkAssetsVersion", "reportCdkAssetsVersion", (yargs) => yargs.positional("version", {
    type: "string",
    describe: "Optionally specify a version to update to",
}), async (args) => {
    console.log('Inside module function being invoked...')
    const { reportCdkAssetsVersion } = await import("sst-aws-cdk/lib/util/asset-publishing.js");
    reportCdkAssetsVersion()
});